ns0:Value.ns0:value:
function package_command {
# Load function arguments into local variables local package_operation=$1 local package=$2
# Check sanity of the input if [ $# -ne "2" ] then echo "Usage: package_command 'install/uninstall' 'rpm_package_name" echo "Aborting." exit 1 fi
# If dnf is installed, use dnf; otherwise, use yum if [ -f "/usr/bin/dnf" ] ; then install_util="/usr/bin/dnf" else install_util="/usr/bin/yum" fi
if [ "$package_operation" != 'remove' ] ; then # If the rpm is not installed, install the rpm if ! /bin/rpm -q --quiet $package; then $install_util -y $package_operation $package fi else # If the rpm is installed, uninstall the rpm if /bin/rpm -q --quiet $package; then $install_util -y $package_operation $package fi fi
}

ns0:Rule.ns0:fix:
grep -R gpgcheck /etc/yum.repos.d/* /etc/yum.conf /root/rpmrc /usr/lib/rpm/redhat/rpmrc /usr/lib/rpm/rpmrc /etc/rpmrc 2>/dev/null | grep -v 'gpgcheck=1' | cut -d: -f1 | sort -u | while read YUM_FILE; do
	sed -i 's/gpgcheck=.*/gpgcheck=1/g' ${YUM_FILE}
done

for UID0_USER in `cat /etc/passwd | cut -d: -f1,3 | grep :0$ | grep -v ^root: | cut -d: -f1`; do
	userdel -rf ${UID0_USER}
done

